			CFEIN VARCHAR(16),
			NOMEIN VARCHAR(254),
			CFRUP VARCHAR(16),
			NOMTEC VARCHAR(161),
			OGGETTO VARCHAR(2000),
			IMPAPP NUMERIC(24,5),
			NOMEST VARCHAR(2000),
			CFIMP VARCHAR(16),
			PIVIMP VARCHAR(16),
			TIPIMP NUMERIC(2),
			IAGGIU  NUMERIC(24,5),
			DATTOA TIMESTAMP,
			SYSCON NUMERIC(12),
			NOTE  VARCHAR(2000),
			PRIMARY KEY(ID));
			
