				else (case when (select count(sezionitec) from gare1 where codgar1=imprdocg.codgar and sezionitec='1') > 0 then 1 else null end) end) end seztec,
			imprdocg.datarilascio,
			imprdocg.orarilascio,
			imprdocg.datascadenza,
			imprdocg.situazdoci,
			imprdocg.doctel,
