		perform sp_backupanddropviews('v_aggiudicatari_stipula');
		create or replace view v_aggiudicatari_stipula (codice,codgar,ngara,codlotto,iaggiu,oggetto,codimp,nomest,codcig,ncont,numcont,nrepat,daatto,modcont,cenint) as
			select case when SUBSTR(t.codgar,1,1)='$' then SUBSTR(t.codgar,2,20) else t.codgar end,ls.codgar1,gc.ngara,ga.ngara,ls.impcomplessivo,t.destor,ls.ditta,i.nomest,ls.codciglotti,gc.ncont,gc.numcont,ls.nrepat,ls.daatto,2,t.cenint
			from gare ga
			join torn t on (ga.ngara=t.codgar)
			join v_lotti_aggregati_aggiud ls on (t.codgar=ls.codgar1)
			join impr i on (ls.ditta=i.codimp)
			join garecont gc on ls.codgar1=gc.ngara and ls.ditta=gc.codimp
			where (ga.genere is null or ga.genere<> 4) and t.modcont=2 and ls.ditta is not null	and not (t.accqua =1 and (t.aqoper=2 or (t.aqoper=1 and gc.esecscig=1 )))
		union
			select case when SUBSTR(t.codgar,1,1)='$' then SUBSTR(t.codgar,2,20) else t.codgar end,ga.codgar1,gc.ngara,ga.ngara,ga.iaggiu,ga.not_gar,ga.ditta,i.nomest,ga.codcig,gc.ncont,gc.numcont,ga.nrepat,ga.daatto,case when coalesce(t.modcont,0)=0 then 0 else 1 end,t.cenint
			from gare ga
			 join torn t on (ga.codgar1=t.codgar) and (ga.ngara<>t.codgar)
			 join impr i on (ga.ditta=i.codimp)
			 join garecont gc on ((gc.ngara=ga.ngara and gc.ncont=1) or (gc.ngara=ga.codgar1 and (gc.ngaral is null or gc.ngaral=ga.ngara)))
			 where (ga.genere is null or ga.genere<> 4) and (t.modcont is null or t.modcont=1) and ga.ditta is not null and not (t.accqua =1 and (t.aqoper=2 or (t.aqoper=1 and gc.esecscig=1 )));
		perform sp_restoreviews('v_aggiudicatari_stipula');

		perform sp_backupanddropviews('v_gare_stipula');
		create or replace view v_gare_stipula as 
			select s.id, vas.codice, vas.codgar, vas.ngara, vas.codlotto, vas.codcig, vas.codimp, i.nomest, s.codstipula, s.oggetto, s.impstipula, s.stato,
			CASE
				WHEN s.stato=3  THEN cast(1 as numeric(12))
				WHEN s.stato=4  THEN cast(2 as numeric(12))
				WHEN s.stato=5  THEN cast(3 as numeric(12))
			END AS stato_portale,
			s.datpub,
			s.syscon as creatore, u1.sysute as nome_creatore, s.assegnatario as contract, u2.sysute as nome_contract, t.cenint,t.codrup, s.isarchi, 
			vas.ncont, vas.numcont, vas.nrepat,
			CASE
				WHEN s.id_padre is null  THEN vas.daatto
				WHEN s.id_padre is not null  THEN s.daatto
			END AS daatto,
			vas.modcont, vas.iaggiu
			from g1stipula s
			join v_aggiudicatari_stipula vas ON s.ngara = vas.ngara AND s.ncont = vas.ncont
			join torn t ON vas.codgar = t.codgar
			left join impr i ON vas.codimp = i.codimp
			left join usrsys u1 ON s.syscon = u1.syscon
			left join usrsys u2 ON s.assegnatario = u2.syscon;	 
		perform sp_restoreviews('v_gare_stipula');


