$$
DECLARE
	ws_g1documod_descrizione varchar(1000);
	g1documod_descrizione varchar(100) := 'Modello di documenti';
	iddocumod_max numeric(7) := 0;
	id_max numeric(7) := 0;
	numord numeric(7) := 0;
	descTabellato varchar(200);
	mydata_ds record;
	mydata record;
	ciclo_archdocg_dist cursor for select DISTINCT GRUPPO, BUSTA, TIPOGARA, TIPOPROC, CRITLIC, GARTEL, LIMINF, LIMSUP from archdocg order by GRUPPO,BUSTA;
	ciclo_archdocg cursor (P_GRUPPO numeric,P_BUSTA numeric,P_TIPOGARA numeric,P_TIPOPROC numeric,P_CRITLIC numeric,P_GARTEL varchar,P_LIMINF numeric,P_LIMSUP numeric) for 
		select reqcap,tipodoc,descrizione,contestoval,obbligatorio,modfirma,allmail,idstampa,idprg,iddocdg from archdocg 
			where GRUPPO=P_GRUPPO and coalesce(BUSTA,0)=P_BUSTA and coalesce(TIPOGARA,0)=P_TIPOGARA and coalesce(TIPOPROC,0)=P_TIPOPROC and 
			coalesce(CRITLIC,0)=P_CRITLIC and coalesce(GARTEL,'nonDefinito')=P_GARTEL and coalesce(LIMINF,-1)=P_LIMINF and coalesce(LIMSUP,-1)=P_LIMSUP order by numdocg;
BEGIN
    IF (select count(*) = 1 from eldaver where codapp = 'PG' and numver = 'M2.9.6.0') THEN
	open ciclo_archdocg_dist;
		loop
			-- Inizializzazione i campi della query ciclo_archdocg_dist
			fetch ciclo_archdocg_dist into mydata_ds;
			-- Se non ci sono più dati da scorrere, esce dal ciclo
			if not found then
				exit;
			end if;
			-- Elaborazione
			iddocumod_max := (select coalesce(max(ID),0) from g1documod)+1;
			ws_g1documod_descrizione := g1documod_descrizione;
			if (mydata_ds.tipogara>0)then
				select tab1desc into descTabellato from tab1 where tab1cod='A1007' and tab1tip=mydata_ds.tipogara;
				ws_g1documod_descrizione := ws_g1documod_descrizione || ' - ' || descTabellato;
			end if;
			if (mydata_ds.tipoproc>0)then
				select tab1desc into descTabellato from tab1 where tab1cod='A2044' and tab1tip=mydata_ds.tipoproc;
				ws_g1documod_descrizione := ws_g1documod_descrizione || ' - ' || descTabellato;
			end if;
			if (mydata_ds.critlic>0)then
				select tab1desc into descTabellato from tab1 where tab1cod='A2081' and tab1tip=mydata_ds.critlic;
				ws_g1documod_descrizione := ws_g1documod_descrizione || ' - ' || descTabellato;
			end if;
			if (mydata_ds.gartel='1')then
				ws_g1documod_descrizione := ws_g1documod_descrizione || ' - Telematica';
			end if;
			insert into g1documod (ID,GRUPPO, BUSTA, TIPOGARA, TIPOPROC, CRITLIC, GARTEL, LIMINF, LIMSUP, DESCRIZIONE) 
