			numord := 0;
			open ciclo_archdocg (mydata_ds.gruppo,coalesce(mydata_ds.busta,0),coalesce(mydata_ds.tipogara,0),coalesce(mydata_ds.tipoproc,0),coalesce(mydata_ds.critlic,0),
