				loop
					-- Inizializzazione i campi della query ciclo_archdocg
					fetch ciclo_archdocg into mydata;
					-- Se non ci sono più dati da scorrere, esce dal ciclo
					if not found then
						exit;
					end if;
					-- Elaborazione
					id_max := (select coalesce(max(ID),0) from g1arcdocumod)+1; 
