CREATE OR REPLACE FUNCTION aggiornamento()
	RETURNS void AS
$$
DECLARE
	maxtip INT;
	conta INT;
	valtab2d2 VARCHAR;
	lentab2d2 INT;
	i Numeric(10);
	tabellato varchar(10);
BEGIN
    IF (select count(*) = 1 from eldaver where codapp = 'PG' and numver = 'M2.9.7.0') THEN
		
		if ((select count(*) from tab1 where tab1cod = 'A2044' and tab1tip=89 and tab1desc='39-AFFIDAMENTO DIRETTO PER MODIFICHE CONTRATTUALI O VARIANTI PER LE QUALI È NECESSARIA UNA NUOVA PROCEDURA DI AFFIDAMENTO') = 0) then
			-- (S.Santi 23.11.2021) Allineamento tabellati in seguito a introduzione tipo procedura per L.190/2021. Script presente anche in LFS/GIL
			-- Aggiunge una riga nel tabellato 'A2044' spostando prima quella con chiave uguale e aggiorna le entità che vi fanno riferimento
			if ((SELECT count(TAB1TIP) FROM TAB1 WHERE TAB1COD='A2044' and TAB1TIP=89 and (TAB1DESC is null or TAB1DESC!='39-AFFIDAMENTO DIRETTO PER MODIFICHE CONTRATTUALI O VARIANTI PER LE QUALI È NECESSARIA UNA NUOVA PROCEDURA DI AFFIDAMENTO')) = 1) then
				maxtip := 0;
				SELECT MAX(TAB1TIP) INTO maxtip FROM TAB1 WHERE TAB1COD='A2044';
				conta := maxtip + 1;
				if(conta < 100) then
					conta := 100;
				end if;
				UPDATE TAB1 SET TAB1TIP=conta WHERE TAB1COD='A2044' and TAB1TIP=89;
				IF exists (select tablename from pg_tables where upper(tablename) = 'GARE') THEN
					UPDATE TORN SET TIPGAR=conta WHERE TIPGAR=89;
					UPDATE GARE SET TIPGARG=conta WHERE TIPGARG=89;
					UPDATE CATPUB SET TIPGAR=conta WHERE TIPGAR=89;
					UPDATE ARCHDOCG SET TIPOPROC=conta WHERE TIPOPROC=89;
					UPDATE CONFOPECO SET TIPOPROCEDURA=conta WHERE TIPOPROCEDURA=89;
				END IF;
				IF exists (select tablename from pg_tables where upper(tablename) = 'PERI') THEN
					UPDATE APPA SET TIPAGG=conta WHERE TIPAGG=89;
