create or replace function sp_v_ws_comunicazioni_per_sa_full() returns void as
$$
declare 
  appa_exists boolean;
begin
  select into appa_exists (select EXISTS (
			SELECT 1 
			FROM   pg_catalog.pg_class c
			JOIN   pg_catalog.pg_namespace n ON n.oid = c.relnamespace
			AND    upper(c.relname) = 'APPA'
			AND    c.relkind = 'r'    --tabelle
			)) ;
  if appa_exists then
    -- ----------------------------------------------------------------------
    -- crea v_ws_comunicazioni_per_sa_full basata su APPA
    -- ----------------------------------------------------------------------
	
create or replace view v_ws_comunicazioni_per_sa_full as 	
	select idprg, idcom, comkey1 nomeuser, comkey2 codice, comkey3 codice2, coment entita, commsgogg oggetto, commsgtes testo, DATE_TRUNC('day', comdatins) datainvio, comdatins dataorainvio, comdatlet 		
dataoralettura, comdatprot dataoraprotocollo, t.cenint, comnumprot numprotocollo, idcfg, committ, commodello, comtipma, comdatsca, comorasca, g.ngara
    from w_invcom c      
	INNER JOIN gare g ON (g.ngara = c.comkey2)
	INNER JOIN torn t ON (t.codgar = g.codgar1)
	where idprg='PA'
	and comtipo='FS12'
	and comstato='3'
	AND coment is null
	UNION ALL
select idprg, idcom, comkey1 nomeuser, comkey2 codice, comkey3 codice2, coment entita, commsgogg oggetto, commsgtes testo, DATE_TRUNC('day', comdatins) datainvio, comdatins dataorainvio, comdatlet 		
dataoralettura, comdatprot dataoraprotocollo, t.cenint,  comnumprot numprotocollo, idcfg, committ, commodello, comtipma, comdatsca, comorasca, g.ngara
    from w_invcom c
	INNER JOIN g1stipula s ON  s.codstipula = c.comkey2
	INNER JOIN gare g ON (g.ngara = s.ngara)
	INNER JOIN torn t ON (t.codgar = g.codgar1)
	where idprg='PA'
	and comtipo='FS12'
	and comstato='3'
	AND coment = 'G1STIPULA'
	UNION ALL
select idprg, idcom, comkey1 nomeuser, comkey2 codice, comkey3 codice2, coment entita, commsgogg oggetto, commsgtes testo, DATE_TRUNC('day', comdatins) datainvio, comdatins dataorainvio, comdatlet 		
dataoralettura, comdatprot dataoraprotocollo, null cenint,  comnumprot numprotocollo, idcfg, committ, commodello, comtipma, comdatsca, comorasca, null ngara
    from w_invcom c
	INNER JOIN appa a ON (a.codlav = c.comkey2 and a.nappal = c.comkey3)
	where idprg='PA'
	and comtipo='FS12'
	and comstato='3'
	AND coment = 'APPA';

  else 
    -- ----------------------------------------------------------------------
    -- crea v_ws_comunicazioni_per_sa_full senza APPA
    -- ----------------------------------------------------------------------
create or replace view v_ws_comunicazioni_per_sa_full as 
select idprg, idcom, comkey1 nomeuser, comkey2 codice, comkey3 codice2, coment entita, commsgogg oggetto, commsgtes testo, DATE_TRUNC('day', comdatins) datainvio, comdatins dataorainvio, comdatlet 		dataoralettura, comdatprot dataoraprotocollo, t.cenint, comnumprot numprotocollo, idcfg, committ, commodello, comtipma, comdatsca, comorasca, g.ngara
    from w_invcom c      
	INNER JOIN gare g ON (g.ngara = c.comkey2)
	INNER JOIN torn t ON (t.codgar = g.codgar1)
	where idprg='PA'
	and comtipo='FS12'
	and comstato='3'
	AND coment is null
	UNION ALL
select idprg, idcom, comkey1 nomeuser, comkey2 codice, comkey3 codice2, coment entita, commsgogg oggetto, commsgtes testo, DATE_TRUNC('day', comdatins) datainvio, comdatins dataorainvio, comdatlet 		dataoralettura, comdatprot dataoraprotocollo, t.cenint, comnumprot numprotocollo, idcfg, committ, commodello, comtipma, comdatsca, comorasca, g.ngara
    from w_invcom c
	INNER JOIN g1stipula s ON  s.codstipula = c.comkey2
	INNER JOIN gare g ON (g.ngara = s.ngara)
	INNER JOIN torn t ON (t.codgar = g.codgar1)
	where idprg='PA'
	and comtipo='FS12'
	and comstato='3'
	AND coment = 'G1STIPULA';

  end if;
end;
$$ language plpgsql;
select sp_v_ws_comunicazioni_per_sa_full();
drop function sp_v_ws_comunicazioni_per_sa_full();  
	
	
