from v_gare_aggiudicate ga 
    inner join torn t on ga.codgar = t.codgar
    inner join uffint u on t.cenint = u.codein
    inner join gare g on ga.codice = g.ngara and ga.ditta = g.ditta
    inner join w_puser ut on ut.userent = 'IMPR' and ((ga.isrt = '0' and ut.userkey1 = g.ditta) or (ga.isrt = '1' and ut.userkey1 = (select coddic from ragimp r where r.codime9 = g.ditta and r.impman = '1')))
    inner join garecont c on ga.codice = c.ngara
    left outer join tab1 ts on ts.tab1cod = 'ME008' and c.stato = ts.tab1tip
    left outer join tab1 ta on ta.tab1cod = 'A2042' and g.tiatto = ta.tab1tip
    left outer join w_docdig doc on doc.idprg = c.idprg and doc.iddocdig = c.iddocdg and doc.digent = 'W_INVCOM' and doc.digkey1 = c.idprg 
    left outer join w_invcomdes des on des.idprg = c.idprg and des.idcom=doc.digkey2 and des.descodsog=ut.userkey1
where ((c.stato is null and g.nrepat is not null) or c.stato in (4,5,6))
and c.iddocdg is not null;


create or replace function sp_v_ws_lfs_contratti() returns void as
$$
declare 
  appa_exists boolean;
begin
  select into appa_exists (select EXISTS (
			SELECT 1 
			FROM   pg_catalog.pg_class c
			JOIN   pg_catalog.pg_namespace n ON n.oid = c.relnamespace
			AND    upper(c.relname) = 'APPA'
			AND    c.relkind = 'r'    --tabelle
			)) ;
  if appa_exists then
    -- ----------------------------------------------------------------------
    -- crea v_ws_lfs_contratti basata su APPA
    -- ----------------------------------------------------------------------
create or replace view v_ws_lfs_contratti as
select 
	a.CODLAV as codice,
	a.nappal,  
	a.notapp as oggetto,	
	a.codcig as cig,	
	c.NIMPCO as importo,
	p.cenint as codiceSA,		
	p.entint as descSA,		
	con1.CONTAB as nominativoDirettoreTecni,	
	con1.nomimp as nominativoDirettoreImpr,
	con2.contab as nominativoRespSicTecni,	
	con2.nomimp as nominativoRespSicImpr,	
	c.DAATTO as dataContratto,
	c.NREPAT as numeroRepertorio,
	ts.tab2d2 as fase,
	w.usernome as usernome
from peri p 
	inner join appa a on (a.codlav = p.codlav)
	inner join cont c on (c.codlav = a.codlav and c.nappal = a.nappal and c.nproat = 1)
	left join contab con1 on (con1.codlav = a.codlav and con1.nappal = a.nappal and con1.inctab = 2)
	left join contab con2 on (con2.codlav = a.codlav and con2.nappal = a.nappal and con2.inctab = 7)
	left outer join tab2 ts on ts.tab2cod = 'A2z58' and a.FASEAPPALTO = ts.tab2tip
	inner join w_invcom i on (i.comkey1 = a.codlav and i.comkey2 = cast( a.nappal as text))
	inner join w_invcomdes des on (des.idprg = i.idprg and des.idcom= i.idcom)
	inner join w_puser w on (w.userkey1 = des.descodsog)
where i.idprg='PL'
and i.coment='APPA'
and (i.comstato='3' or i.comstato='4' or i.comstato='10' or i.comstato='11')
union
select 
	a.CODLAV as codice,
	a.nappal,  
	a.notapp as oggetto,	
	a.codcig as cig,	
	c.NIMPCO as importo,
	p.cenint as codiceSA,		
	p.entint as descSA,		
	con1.CONTAB as nominativoDirettoreTecni,	
	con1.nomimp as nominativoDirettoreImpr,
	con2.contab as nominativoRespSicTecni,	
	con2.nomimp as nominativoRespSicImpr,	
	c.DAATTO as dataContratto,
	c.NREPAT as numeroRepertorio,
	ts.tab2d2 as fase,
	w.usernome as usernome
from peri p 
	inner join appa a on (a.codlav = p.codlav)
	inner join cont c on (c.codlav = a.codlav and c.nappal = a.nappal and c.nproat = 1)
	left join contab con1 on (con1.codlav = a.codlav and con1.nappal = a.nappal and con1.inctab = 2)
	left join contab con2 on (con2.codlav = a.codlav and con2.nappal = a.nappal and con2.inctab = 7)
	left outer join tab2 ts on ts.tab2cod = 'A2z58' and a.FASEAPPALTO = ts.tab2tip
	inner join w_invcom i on (i.comkey1 = a.codlav and i.comkey2 = cast( a.nappal as text))
	inner join w_invcomdes des on (des.idprg = i.idprg and des.idcom= i.idcom)
	inner join ragimp r on (r.codime9 = des.descodsog)
	inner join w_puser w on (w.userkey1 = r.coddic)
where i.idprg='PL'
and i.coment='APPA'
and (i.comstato='3' or i.comstato='4' or i.comstato='10' or i.comstato='11')
and r.impman = '1';


  else 
    -- ----------------------------------------------------------------------
    -- crea v_ws_odalfs senza APPA
    -- ----------------------------------------------------------------------
create or replace view v_ws_lfs_contratti as
select 
	null::character varying(20) as codice,
	null::numeric(7,0) as nappal,  
	null::character varying(2000) as oggetto,	
	null::character varying(20) as cig,	
	null::numeric(24,5) as importo,
	null::character varying(16) as codiceSA,		
	null::character varying(254) as descSA,		
	null::character varying(161) as nominativoDirettoreTecni,	
	null::character varying(61) as nominativoDirettoreImpr,
	null::character varying(161) as nominativoRespSicTecni,	
	null::character varying(61) as nominativoRespSicImpr,	
	null::timestamp without time zone as dataContratto,
	null::character varying(20) as numeroRepertorio,
	null::character varying(200) as fase,
	null::character varying(20) as usernome
from eldaver where 1=0;
	
  end if;
end;
$$ language plpgsql;
select sp_v_ws_lfs_contratti();
drop function sp_v_ws_lfs_contratti();
	



