	select 
		ngara codice,
		numord,
		dittao ditta, 
		nomest ragsoc,
		cfimp codfisc, 
		pivimp partiva,
		case when tipimp in (3,10) then 1 else 0 end rti
	from ditgaq r left outer join impr i on (r.dittao = i.codimp);

