create or replace view v_ws_trasp_contratti_dett_rti as
	select r.codime9 codimprti, 
		r.nomdic ragsoc, 
		i.cfimp codfisc, 
		i.pivimp partiva,
		case when r.impman = 1 then 1 else 0 end mandataria,
		r.coddic codimp
		from ragimp r left outer join impr i on (r.coddic = i.codimp);


-- view per predisporre l'estrazione dei dati nelle diverse fasi di espletamento gara
create or replace view v_ws_fasi_gara_partecipanti as
select
    fasi.tab1tip as num_fase,
    fasi.tab1desc as desc_fase,
    d.codgar5 as codgar,
    d.ngara5 as codice,
	case when (d.codgar5 not like '$%') and d.codgar5 <> d.ngara5 then d.codgar5 else g.ngara end as ngara,
    case when (d.codgar5 not like '$%') and d.codgar5 <> d.ngara5 then d.ngara5 else null end as lotto,
	g.codiga as codinternolotto,
    case when (d.codgar5 not like '$%') and d.codgar5 <> d.ngara5 then g.not_gar else null end as oggettolotto,
    d.dittao as codopeconomico,
    wd.nomimo as denopeconomico,
    i.cfimp as cfopeconomico,
    case when wd.codati is null then 0 else 1 end as rti,
    u.usernome,
    d.numordpl as numplico,	
    case when d.partgar is null then '0'
         when fasi.tab1tip in (-4, 2, 5, 6) then d.partgar
         else '0'
    end as partecipalotto,

	case when da.fasgar = -4 then null
         when d.partgar = '2' and d.fasgar in (-4, 2, 5, 6) and fasi.tab1tip >= d.fasgar then null
	     when fasi.tab1tip < da.fasgar then 
	          (select max(b.ammgar) from ditgammis b where b.codgar = da.codgar and b.ngara = da.ngara and b.dittao = da.dittao and b.fasgar <= fasi.tab1tip) 
	     else da.ammgar  
    end as ammissione,

	case when da.fasgar = -4 then null
         when d.partgar = '2' and d.fasgar in (-4, 2, 5, 6) and fasi.tab1tip >= d.fasgar then null
	     when fasi.tab1tip < da.fasgar then 
			  (select t.tab1desc from tab1 t where t.tab1cod = 'A1054' and t.tab1tip = 
	                (select max(b.ammgar) from ditgammis b where b.codgar = da.codgar and b.ngara = da.ngara and b.dittao = da.dittao and b.fasgar <= fasi.tab1tip))
	     else (select t.tab1desc from tab1 t where t.tab1cod = 'A1054' and t.tab1tip = da.ammgar)
    end as descammissione,

	d.ncomope as progofferta
from ditg d
     left outer join ditgammis da on (da.codgar = d.codgar5 and da.ngara = d.ngara5 and da.dittao = d.dittao)
     inner join gare g on g.codgar1 = d.codgar5 and g.ngara = d.ngara5
     inner join gare1 g1 on g1.codgar1 = g.codgar1 and g1.ngara = g.ngara
     inner join v_ws_ditg wd on wd.codgar5 = d.codgar5 and wd.ngara5 = d.ngara5 and wd.dittao = d.dittao
     inner join impr i on i.codimp = d.dittao
     inner join w_puser u on u.userent = 'IMPR' and u.userkey1 = wd.codman,
     (select tab1.tab1cod,
             tab1.tab1tip,
             tab1.tab1desc,
             tab1.tab1mod,
             tab1.tab1nord,
             tab1.tab1arc
        from tab1
       where tab1.tab1cod = 'A1011') fasi
where (fasi.tab1tip in (1,2,5,6,7,8)) 
and (da.fasgar is null or da.fasgar = -4 or da.fasgar >= 1)
and (not exists (select a.ammgar
				   from ditgammis a 
				  where a.codgar = d.codgar5 and a.ngara = d.ngara5 and a.dittao = d.dittao and a.fasgar > da.fasgar)
)
and (
  -- ditg.fasgar = null ditta non esclusa
  (d.fasgar is null)
  -- ditg.fasgar <> null ditta esclusa
  or (d.fasgar is not null and da.fasgar is null and fasi.tab1tip <= d.fasgar)
  or (d.fasgar is not null and fasi.tab1tip <= d.fasgar and d.fasgar = da.fasgar)
  or (d.fasgar is not null and da.fasgar is not null and fasi.tab1tip <= d.fasgar)
)
and (
  -- gare a lotti
  (d.codgar5 = d.ngara5 and fasi.tab1tip in (-4,1,2) )
  or (d.codgar5 = d.ngara5 and g.bustalotti = 1 and not fasi.tab1tip in (5,6,7,8))
  or (d.codgar5 = d.ngara5 and g.bustalotti <> 1 and fasi.tab1tip in (5,6))
  or (d.codgar5 <> d.ngara5)
)
and (
  -- gare con valutazione tecnica
  (fasi.tab1tip = 5 and (g.modlicg=6 or g1.valtec = 1))
  or (fasi.tab1tip <> 5)
);

-- view per estrarre i dati in fase di apertura documentazione amministrativa
create or replace view v_ws_apertura_doc_amm as
select p.codgar, p.codice, p.ngara, p.lotto, p.codinternolotto, p.oggettolotto, 
